 * @param mapping_version DIVA mapping version generated by Chronos during a Parser or RealtimeBusConfig load
 */
case class V3.SiriReferenceDataRequest (
  line_refs: List[V3.SiriLineRefDirectionRefStopPointRef],
  stop_point_refs: Option[List[Int]],
  date_utc: Option[Date],
  mapping_version: String
)

